export class showResultService {
  constructor() {
    this.number2 = 0;
  }

  onResult(number, calculation, calculationType) {
    if (number !== 0) {
      this.number2 = Number(calculation);

      if (calculationType === "+") {
        calculation = String(number + this.number2);
      } else if (calculationType === "-") {
        calculation = String(number - this.number2);
      } else if (calculationType === "*") {
        calculation = String(number * this.number2);
      } else if (calculationType === "/") {
        calculation = String(number / this.number2);
      }
    } else {
      calculation = "";
    }

    this.number2 = 0;
    return calculation;
  }

}
